function getRow() {
	var table = document.getElementById('table');
	for (var i = 0; i < table.rows.length; i++) {
		table.rows[i].onclick = function() {
			document.getElementById("payment_id").value = this.cells[1].textContent;
			document.getElementById("name_on_card").value = this.cells[2].textContent;
			document.getElementById("card_number").value = this.cells[3].textContent;
			document.getElementById("cvv").value = this.cells[4].textContent;
			document.getElementById("exp_day").value = this.cells[5].textContent;
			document.getElementById("amout").value = this.cells[6].textContent;
		};
	}
}

