function load() {
	$.ajax({
		url : 'http://localhost:8080/payment/webresources/Payment_managementResources/Payment_managements',
		method : 'GET',
		headers : {
			Accept : "application/json",
			"Content-Type" : "application/json"
		},
		success : function(data, textStatus, errorThrown) {
			var items = [];
			$.each(data.paymentManagement,function(key, val) {
				var index = key + 1;
				items.push("<tr>");
				items.push("<td>" + index + "</td>");
				items.push("<td>" + val.payment_id + "</td>");
				items.push("<td>" + val.name_on_card + "</td>");
				items.push("<td>" + val.card_number + "</td>");
				items.push("<td>" + val.cvv + "</td>");
				items.push("<td>" + val.exp_day + "</td>");
				items.push("<td>" + val.amout + "</td>");
				items.push("<td><button onclick='getRow()' type='button' class='btn btn-info btn-fill'>Edit</button></td>");
				items.push("<td><button onclick='delet()' type='button' class='btn btn-danger btn-fill'>Delete</button></td>");
				items.push("</tr>");
			});
		$("<tbody/>", {
			html : items.join("")
		}).appendTo("#table");
		},
		error : function(jqXHR, textStatus, errorThrown) {
			alert("Ajax request fail");
		},
		timeout : 120000,
	});
}

function getJSON() {
	return JSON.stringify({
		"payment_id" : $('#payment_id').val(),
		"name_on_card" : $('#name_on_card').val(),
		"card_number" : $('#card_number').val(),
		"cvv" : $('#cvv').val(),
		"exp_day" : $('#exp_day').val(),
		"amout" : $('#amout').val(),
	});
}

